import { HANDLE_INIT_PRODUCT } from "./constants";
const initialState = {
    products: [],
    listInerProduct: []
}
const productReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'HANDLE_INIT_PRODUCT': {
            console.log('abc', action.payload, state.listInerProduct);

            return {
                ...state,
                listInerProduct: action.payload
            }
        }
        default: 
        return state;
    }
}
export default productReducer;
