import styled from "styled-components";
export const HeaderBottomItems = styled.li`
    position: relative;
    cursor: pointer;
    z-index: 3;
    padding: 0.75rem 1rem;
    &:hover > .drop-menu {
        opacity: 1;
        visibility: visible;
    }
    .item-link {
        display: block;
        width: 100%;
        height: 100%;
        color: #000;
        font-size: 1rem;
        font-weight: 700;
        text-transform: uppercase;
        &.active-menu {
            border-bottom: 2px solid #f89537;
            color: #f89537;
            font-size: 1.2rem;
        }
    }
`
export const DropdownMenuWrapper = styled.ul`
    position: absolute;
    top: 102%;
    left: 0;
    min-width: 320px;
    background: #ffffff;
    box-shadow: 1px 1px 15px rgb(0 0 0 / 15%);
    border: 2px solid #ddd;
    border-radius: 3px;
    margin-top: 1rem;
    padding: 0%;
    opacity: 0;
    visibility: hidden;
    transition: all .3s ease-in-out;
    &:after {
        position: absolute;
        border: 20px solid #fff;
        border-left-color: transparent;
        border-right-color: transparent;
        border-top-color: transparent;
        content: '';
        bottom: 100%;
        left: 2%;
    }
`
export const DropdownMenuitems = styled.li`
    padding: .65rem 0.75rem;
    border-bottom: 1px solid #ddd;
    &:first-letter {
                text-transform: capitalize;
    }
    .drop-link-menu {
            color: #000;
            &.active-drop-menu {
                color: #f89537;
                font-size: 1.2rem;
            }
            
    }
`
