import styled from "styled-components";
export const InputWrapper = styled.div`
    display: flex;
    flex-direction: ${props => props.nameLabel === 'Hiển thị mật khẩu' ? 'row' : 'column'};
    align-items: ${props => props.nameLabel === 'Hiển thị mật khẩu' ? 'center' : 'flex-start'};
    position: relative;
    border: 1px solid;
    input[type="text"],input[type="email"],input[type="password"] {
         padding: .25rem 2rem .25rem .25rem;
         margin: 0.75rem 0;
         outline: none;
         width: 100%;
    }
    input[type="submit"] {
        padding: .35rem 4rem;
        font-weight: bold;
        text-transform: uppercase;
        font-size: 1.3rem;
    }
    input[type="checkbox"], input[type="radio"] {
        width: auto;
        margin: 0 .2rem;
    }
    label {
        font-size: 1.2rem;
        font-weight: 700;
        color: ${props => props.nameLabel ==="Hiển thị mật khẩu" ? "#fff" : "#434E5E"};
    }
    .icon {
        position: absolute;
        top: 50%;
        left: 0;
    }

`
