import styled from "styled-components";
export const IconCards = styled.span`

                    display: block;
                    width: 2.2rem;
                    padding: .25rem .35rem;
                    border: 2px solid #fbb702;
                    text-align: center;
                    position: relative;
                    z-index: 3;
                    border-radius: 3px;
                    transition: .3s;
                    font-weight: 700;
                    color: #fbb702;
                    &:after {
                        width: 13px;
                        height: 14px;
                        border-top-left-radius: 50%;
                        border-top-right-radius: 50%;
                        position: absolute;
                        bottom: 80%;
                        left: 50%;
                        transform: translate(-50%,0);
                        content: '';
                        border: 1px solid #fbb702;
                        border-bottom: 0;
                        z-index: 2;
                        transition: .3s;
                    }
                    &:hover {
                        background-color: #fbb702;
                        color: #fff;
                    }
`
