import styled from "styled-components";
export const ProductItems = styled.div`
    padding: .45rem;
    transition: .3s;
    @media (max-width: 575px) {
        width: 80%;
    }
    &:hover {
        border: 1px solid #ddd;
    }
    & > img {
        width: 100%;
        margin: 0.25rem 0 1rem;
        object-fit: cover;
        transition: .3s;
        transform: scale(0.8);
        cursor: pointer;
        &:hover {
            transform: scale(1);
        }
    }
    .name-product {
        max-width: 80%;
        font-size: 1.1rem;
        font-weight: 550;
        color: #434E5E;
        min-height: 90px;
        cursor: pointer;
    }
    .price-item {
        color: red;
        position: relative;
        font-size: 1.3rem;
        margin-left: .25rem;
        &:after {
            content: 'đ';
            text-decoration: underline;
        }
    }
    .discount {
        color: #434E5E;
        text-decoration: line-through;
        display: ${props => props.discount === '' ? 'none' : 'inline'};
        &:after {
            content: 'đ';
            text-decoration: underline;
        }
    }
    .btn-card {
        display: block;
        width: 100%;
        padding: 1.1rem 0;
        margin: 1rem 0;
        text-transform: uppercase;
        background: rgba(27 ,26, 32, 0.8);
        color: #fff;
        transition: .3s;
        &:hover {
            background: rgba(27 ,26, 32,1);
        }
    }
`
