import { useState,useEffect } from 'react';
export const Loading = () => {
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false)
    }, 3000);
  },[]);
  return isLoading;
}
