import React, { useEffect, useState } from "react";
import { FootwearWrapper, HeadTabs } from "./style";
import { connect } from "react-redux";
import { ProductTabs } from "../Home/data";
import { handleInitProduct } from "./action";
import ProductItem from "../Home/Component/ProductItem";
import ReactPaginate from 'react-paginate';
import PaginationPage  from "./Component/Pagination";
import TitlePage from "../../Component/Title";
import NamePage from "../../Component/NamePage";
import SelectOption from "../../Component/SelectOption";
import _ from 'lodash';
const Name = [
    {
        id: 0,
        name: 'Tên'
    },
    {
        id: 1,
        name: 'Từ A - Z'
    },
    {
        id: 2,
        name: 'Từ Z - A'
    }
]
const Footwear = props => {
    const {handleInitProduct, products} = props;
    const {product} = products
    useEffect(() => {
        handleInitProduct(ProductTabs)
    },[]);
    const footWear = _.filter(product, item => item.tabs === "sneaker");
    // onChange select
    const [select, setSelect] = useState(0)
    // sort by name
    const [listProductSortBy, setSortByName] = useState([]);
    const handleSelectChange = event => {
        const {value} = event.target;
        setSelect(value)
        console.log('value', value);
    }
    return(
        <FootwearWrapper className="product">
            <TitlePage name="Giày Dép"/>
            <div className="page container">
                <HeadTabs className="head-tabs">
                    <NamePage nameLink="Trang chủ / " to="/" name="Giày Dép" />
                    <SelectOption nameSort="Sắp xếp theo" value={select} handleSelectChange={handleSelectChange} nameSelect="Name" option={Name}/>
                </HeadTabs>
                <PaginationPage product={footWear}/>
            </div>
        </FootwearWrapper>
    );
}
const mapStateToProps = state => {
    return {
        products: state. productFootWaer
    }
}
const mapDispatchToProps = dispatch => {
    return {
        handleInitProduct: (data) => dispatch(handleInitProduct(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps) (Footwear);
