import React, { useEffect } from "react";
import {
  getListProductDetailRequest
} from "./action";
import { connect } from "react-redux";
import {
  selectDetail,
  selectIsLoading
} from "./selector"
import Loading from "../Components/Loading";
import { DetailWrapper, TitlePageItems } from "./style";
import NamePage from "../Components/NamePage";
const TitlePageItem = ({titlePageItem, history}) => {
  const handleClickToHome = () => {
    history.push("/")
  }
  return(
    <TitlePageItems onClick={titlePageItem.path ? handleClickToHome : null}>
      {titlePageItem.name}
    </TitlePageItems>
  );
}
const Detail = (props) => {
  const {
    handleGetListProductDetail,
    history,
    match: { params: { id } },
    detail,
    isLoading
  } = props;
  useEffect(() => {
    handleGetListProductDetail(+id)
  }, [])
  if (isLoading) return <Loading />
  // hiển thị info
  const {
    avatar,
    color,
    description,
    discount,
    discountContent,
    evaluate,
    like,
    name,
    price,
    promotion,
    quantity,
    questionAnswer,
    ship,
    size,
  } = detail;
  return (
    <DetailWrapper className="detail-wrapper">
      <div className="container">
        <ul className="list-title-page">
          {titlePages.map(item => <TitlePageItem
          titlePageItem={item}
          key={item.id}
          history={history}
          />)}
        </ul>
      </div>
    </DetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    isLoading: selectIsLoading(state),
    detail: selectDetail(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductDetail: data => dispatch(getListProductDetailRequest(data))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Detail);
