import styled from "styled-components";
export const RelateProductItems = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin: .65rem .25rem;
    & > img {
      width: 30%;
    }
    .info {
      width: 65%;
      .name-product {
        max-width: 400px;
        display: -webkit-box;
        font-size: 1rem;
        line-height: 1.3;
        height: 1rem*1.3*3;
        text-overflow: ellipsis;
        overflow: hidden;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
      }
      .price {
        display: flex;
        & > p {
          margin-right: .25rem;
        }
        .price-text {
          color: red;
        }
      }
    }
`
