import styled from "styled-components";
export const HeaderWrapper = styled.div`
    .header-top {
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: .25rem 3rem;
        background: #020202;
        & > img {
            width: 20%;
            cursor: pointer;
        }
        .group-card {
            display: flex;
            width: 40%;
            border: 1px solid #fff;
            .search {
                position: relative;
                & > .input-search {
                    position: absolute;
                    width: 100%;
                    height: 100%;
                }
            }
            .card {
                display:flex;
                flex-direction: row;
                align-items: center;
                border: 3px solid red;
              
            }
        }
    }
`
