import styled from "styled-components";
export const CartWrapper = styled.div`
  padding: 3rem;
  & > .container {
    display: flex;
    box-shadow: ;
    .payment {
      width: 25%;
      height: 500px;
      border: 1px solid;
    }
    .list-product-cart {
      flex: 1;
      background: red;
