const HeaderBottomItem = props => {
    const { item } = props;
    const { name, dropdown } = item;
    const {state, setState} = useState({
        statusDrop: false
    })
    const handleStatusDrop = () => {
        setState({
            ...state,
            statusDrop: !state.statusDrop
        });
    }
    console.log('drp[', statusDrop);
    return (
        <HeaderBottomItems className="menu-item" onClick={handleStatusDrop}>
            <NavLink to="" className="item-link">
                {name}
            </NavLink>
        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
