import { useState,useEffect } from 'react';
export const Loading = () => {
  const [isLoading, setIsLoading] = useState(false);
  useEffect(() => {
    setIsLoading(false);
    setTimeout(() => {
      setIsLoading(true)
    }, 3000);
  },[]);
  return isLoading;
}
