import React from 'react';
import { SelectOptionWrapper } from './style';
const SelectOption = props => {
    const {option} = props;
    return(
        <SelectOptionWrapper value="" name="" className="">
            {option.map(item => {
                return <option value={item.id}>
                    {item.name}
                </option>
            })}
        </SelectOptionWrapper>
    );
}
export default SelectOption;
