import styled from "styled-components";
export const ProductItems = styled.div`
    padding: .45rem;
    & > img {
        width: 100%;
        margin: 0.25rem 0 1rem;
        object-fit: cover;
    }
    .name-product {
        font-size: 1.2rem;
        font-weight: 550;
        color: #434E5E;
        min-height: 75px;
    }
    .price-item {
        color: red;
        position: relative;
        font-size: 1.3rem;
        margin-left: .25rem;
        &:after {
            content: 'đ';
            text-decoration: underline;
        }
    }
`
