import styled from "styled-components";
export const HomeWrapper = styled.div`
    padding-bottom: 2.5rem;
`
export const BannerWrapper = styled.div`
    width: 100%;
    height: 70vh;
    overflow: hidden;
    .slick-prev {
        left: 0;
        background: red;
        &:before {
            width: 50px;
            height: 50px;
            background: red;
        }
    }

`
