import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem'
const Home = props => {
    const [state, setState] = useState({
        slider: [],
        listInnerProduct: []
    })
    console.log('slider', state.slider);
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem,
            listInnerProduct: ListInnerProduct
        })
    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    console.log('list', state.listInnerProduct);
    const listSelectMenu = ['giày', 'dép', 'áo', 'quần', 'balo', 'túi xách', 'áo nam', 'áo nữ', 'phụ kiện khác']
    return (
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index} />
                        })}
                    </Slider>
                </BannerWrapper>
                <div className="container inner">
                    <div className="row">
                        {state.listInnerProduct.map(item => {
                            return <InnerItem item={item} key={item.id} />
                        })}
                    </div>
                </div>
            </div>

        </HomeWrapper>
    );
}
export default Home;
