import styled from "styled-components";
export const FooterWrapper = styled.div`
    background: #171717;
    padding: 2rem .15rem;
    .footer-left {
        color: #fff;
        .descript-footer {
            margin: 1rem;
            text-align: justify;
        }
    }
    .footer-main {
        & > ul {
            color: #fff;
            margin: 1rem;
            padding: 0;
        }
    }
`
export const FooterItems = styled.li`
    & > svg {
        margin-right: 1rem;
        min-width: 20px;
    }
`
