import styled from "styled-components";
export const HeaderBottomItems = styled.li`
  
    .item-link {
        padding: 0.75rem 1rem;
        color: #000;
        font-size: 1.2rem;
        font-weight: 700;
        text-transform: uppercase;
        &.active-menu {
            border-bottom: 2px solid red;
        }
    }
`
