export const DetailWrapper = styled.div`

`
export const ProductDetailWrapper = styled.div`
  display: flex;
  width: 100%;
  .slider-images,.select-delivery {
    width: 25%;
    padding: 1.2rem 1rem;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
    padding: 1.2rem 1rem;
    .name-product {
      font-size: 1.5rem;
      color: #212121;
    }
    .evaluate {
      width: 100%;
      display: flex;
