import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper, ListProductWrapper, TabItems } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem';
import { NavLink } from 'react-router-dom';
import _ from 'lodash';
const TabItem = props => {
    const { item, onClick , classActive} = props;
    return (
        <TabItems className={`tab-item ${classActive}`} onClick={onClick} >
            {item.name}
        </TabItems>
    );


}
const Home = props => {
    const [state, setState] = useState({
        slider: [],
        listInnerProduct: [],
        // listTabMenu: [
        //     {
        //         id: 1,
        //         name: 'sản phẩm mới',
        //         status: true
        //     },
        //     {
        //         id: 2,
        //         name: 'sản phẩm bán chạy',
        //         status: false
        //     },
        //     {
        //         id: 3,
        //         name: 'sản phẩm phổ biến',
        //         status: false
        //     }
        // ]
    })
    
    console.log('slider', state.slider);
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem,
            listInnerProduct: ListInnerProduct
        })
    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    const [exist, setExits] = useState(true);
    const listTabMenu = [
        {
            id: 1,
            name: 'sản phẩm mới',
            status: true
        },
        {
            id: 2,
            name: 'sản phẩm bán chạy',
            status: false
        },
        {
            id: 3,
            name: 'sản phẩm phổ biến',
            status: false
        }
    ]
   


 
   
   
    const [tabs, setTabs] = useState(1)
    const toggleTab = index => {
        console.log('index', index);
        setTabs(index)
    }
    return (
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index} />
                        })}
                    </Slider>
                </BannerWrapper>
                <div className=" inner">
                    <div className="row">
                        {state.listInnerProduct.map(item => {
                            return <InnerItem item={item} key={item.id} />
                        })}
                    </div>
                </div>
                <ListProductWrapper className="list-product">
                   <div className=""></div>
                </ListProductWrapper>
            </div>

        </HomeWrapper>
    );
}
export default Home;
