import styled from "styled-components";
export const MessageCartWrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: red;
  color: #fff;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 999999999;
  padding: 3rem;
  .message-content {
    width: 80%;
    height: 80%;
    background: #fff;
  }
`
