import styled from "styled-components";
export const FooterWrapper = styled.div`
    background: #171717;
    padding: 2rem .15rem 0;
    .container {
        .footer-left {
        color: #fff;
        .descript-footer {
            margin: 1rem;
            text-align: justify;
            &:::first-letter 
        }
    }
    .footer-main {
        & > ul {
            color: #fff;
            margin: 1rem;
            padding: 0;
        }
    }
    .footer-right {
        & > ul {
            color: #fff;
            margin: 1rem;
            padding: 0;
            display: flex;
            @media (max-width: 340px) {
                flex-wrap: wrap;
            }
        }
    }
}
   
    .footer-bottom {
        color: #777;
        font-weight: bold;
        text-align: center;
        background: #000;
        .content {
            margin: 0;
            padding: 0.35rem 0;
            
        }
    }
`
export const FooterItems = styled.li`
    margin: 1.1rem 0;
    & > svg {
        margin-right: 1rem;
        min-width: 20px;
    }
`
export const SocialItems = styled.li`
    min-width: 50px;
    min-height: 50px;
    margin: 0 .25rem;
    background: ${props => props.bg};
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #fff;
    font-size: 1.2rem;
    cursor: pointer;
    & > a {
        color: #fff
    }
    @media (max-width: 340px) {
                margin: .25rem;
            }
`
