import { createSelector } from "reselect";
const selectHeaderReducer = state => state.header;
export const selectProducts = createSelector(
  selectHeaderReducer,
  state => state.products
)
export const selectCounter = createSelector(
  selectHeaderReducer,
  state => state.counter
)
