import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper, ListProductWrapper, TabItems , CarouselProductWrapper} from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct, ProductsTabs } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem';
import { NavLink } from 'react-router-dom';
import ProductItem from './Component/ProductItem';
import _ from 'lodash';
import _uniq from 'lodash/uniq';
import _filter from 'lodash/filter';
import _intersection from 'lodash/intersection'
import './style.css';
import { connect } from 'react-redux';
import { handleInitProduct } from './actions';
const TabItem = props => {
    const {ind, item, onClick, classActive } = props;
    return (
        <TabItems className={`tab-item ${classActive}`} onClick={onClick} >
            {item}
        </TabItems>
    );


}
const Home = props => {
    const {handleInitListProduct} = props
    const [state, setState] = useState({
        slider: [],

        productTabs: []
    })
    const [filterTabs, setFilterTabs] = useState([]);
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem,
            productTabs: ProductsTabs
        })
        handleInitListProduct(listInnerProduct)

    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    const listTabMenu = ['sản phẩm mới', 'sản phẩm bán chạy','sản phẩm phổ biến']
    // xử lý tabs
    const Tabs = {
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 2
    }





    const [tabs, setTabs] = useState(0)
    const toggleTab = (id,item) => {
        setTabs(id);
        const news = _uniq([item]);
        setFilterTabs(news)
    }
    const filterProduct = () => {
        const data = state.productTabs.filter(item => {
            const {type} = item;
            const news = _intersection(filterTabs, type)
            const check = news.length ? true : false;
            return check
        })
        return data;
    }
    const data = filterTabs.length ? filterProduct() : state.productTabs
   console.log('dataa', data);
    return (
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index} />
                        })}
                    </Slider>
                </BannerWrapper>
                <div className=" inner">
                    <div className="row">
                        {state.listInnerProduct.map(item => {
                            return <InnerItem item={item} key={item.id} />
                        })}
                    </div>
                </div>
                <ListProductWrapper className="list-product">
                    <ul className="list-tab">
                        {listTabMenu.map((item, ind) => {
                            return <TabItem item={item} key={ind} ind={ind} classActive={tabs === ind  ? 'active-tab' : ''} onClick={() => toggleTab(ind,item)} />
                        })}
                    </ul>
                    <div className="product tabs">
                        <Slider {...Tabs}>
                            {data.map(item => {
                                return <ProductItem item={item} key={item.id} />
                            })}
           
                        </Slider>
                        {data.length  ? "" : <div className="empty">Sản phẩm đã hết</div>}
                    </div>
                </ListProductWrapper>
            </div>

        </HomeWrapper>
    );
}
const mapStateToProps = state => {
    return {
        listInnerProducts: state.listInnerProduct
    }
}
const mapDispatchToProps = dispatch => {
    return {
        handleInitListProduct: data => dispatch(handleInitProduct(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps) (Home);
