import styled from "styled-components";
export const SearchProductItems = styled.div`
    margin: 0.75rem 0;
    display: flex;
    align-items: center;
    justify-content: space-between;

    &> img {
        width: 30%;
    }
    .info {
        width: 60%;
    }
`
