import styled from "styled-components";
export const BannerImageItems = styled.li`
  width: 100%;
  height: ${props => props.type && 'calc(100%/2)'};
  padding: .55rem;
  & > a img {
    width: 100%;
    height: 100%;
    border-radius: 10px;
    box-shadow: rgba(0, 0, 0, 0.16) 0px 3px 6px, rgba(0, 0, 0, 0.23) 0px 3px 6px;
  }
`
