import styled,{keyframes} from "styled-components";
const moveBorderForm = keyframes`
    0% {
        left: 0;
    }
    50% {
        left: 50%;
    }
    100% {
        left: 100%;
    }
`
export const LoginWrapper = styled.div`
    margin: 4rem;
    & > .container {
        width: 40%;
        .name-form {
            padding: 1rem;
            font-size: 2.5rem;
            text-transform: uppercase;
            color: #434E5E;
            position: relative;
            display: block;
            width: fit-content;
            margin: auto;
            &:after {
                position: absolute;
                top: 100%;
                left: 0;
                width: 20%;
                content: '';
                border: 2px solid #020202;
                background: #020202;
                animation: ${moveBorderForm} 3s linear infinite alternate-reverse;
                
            }
        
        }
        form .submit-form {
            
        }
    }
`
