import styled from "styled-components";
export const FooterWrapper = styled.div`
    background: #171717;
    padding: 2rem .15rem;
    .footer-left {
        color: #fff;
        .descript-footer {
            margin: 1rem;
            text-align: justify;
        }
    }
    .footer-main {
        & > ul {
            color: #fff;
            margin: 1rem;
            padding: 0;
        }
    }
    .footer-right {
        & > ul {
            color: #fff;
            margin: 1rem;
            padding: 0;
            display: flex;
        }
    }
`
export const FooterItems = styled.li`
    margin: 1.1rem 0;
    & > svg {
        margin-right: 1rem;
        min-width: 20px;
    }
`
export const SocialItems = styled.li`
    min-width: 50px;
    min-height: 50px;
    margin: 0 1rem
    background: #000;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #fff;
`
