import styled from "styled-components";
export const ProductItems = styled.div`
    padding: .45rem;
    & > img {
        width: 100%;
        margin: 0.25rem 0 1rem;
        object-fit: cover;
    }
    .name-product {
        font-size: 1.4rem;
        font
    }
`
