import styled from "styled-components";
export const RelateProductItems = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    & > img {
      width: 30%;
    }
    .info {
      width: 65%;
      font-size: 1.3rem;
      .name-product {
        display: block;
  display: -webkit-box;

  height: 16px*1.3*3; /* Fallback for non-webkit */
  margin: 0 auto;
  font-size: 16px;
  line-height: 1.3;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
  text-overflow: ellipsis;
  background: tomato;
  color: #000;
      }
    }
`
