import styled from "styled-components";
export const HeaderWrapper = styled.div`
    .header-top {
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: .25rem 3rem;
        background: #020202;
        & > img {
            width: 20%;
            cursor: pointer;
        }
        .group-card {
            display: flex;
            position: relative;
            width: 20%;
            .search {
          
                .icon-search {
                    padding: 0.45rem;
                    margin-right: .35rem;
                    color: #fff;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    border-right: 1px solid #ddd;
                }
                & > .input-search {
                    position: absolute;
                    top: 100%;
                    left: 0;
                    width: 100%;
                    border-radius: 3px;
                    padding: 1.2rem;
                    box-shadow: 1px 1px 15px rgb(0 0 0 / 15%);
                    background: #fff;
                    border: 1.5px solid #ddd;
                    &:after {
                        position: absolute;
                        width: 40px;
                        height: 40px;
                        background: red;
                        content: '';
                        bottom: 100%;
                        left: 0;
                        
                    }
                }
            }
            .card {
                display:flex;
                flex-direction: row;
                padding: 0 0.35rem;
                background: transparent;
                color: #fff;
                .icon-card {
                    display: block;
                    width: 2.2rem;
                    height: 100%;
                    border: 2px solid #fff;
                    text-align: center;
                    position: relative;
                    z-index: 3;
                    margin-left: .55rem;
                    border-radius: 3px;
                    &:after {
                        width: 13px;
                        height: 14px;
                        border-top-left-radius: 50%;
                        border-top-right-radius: 50%;
                        position: absolute;
                        bottom: 80%;
                        left: 50%;
                        transform: translate(-50%,0);
                        content: '';
                        border: 1px solid #fff;
                        border-bottom: 0;
                        z-index: 2;
                    }
                    
                }
              
            }
        }
    }
`
