import styled from "styled-components";
export const TabItems = styled.li`
  & > a {
    padding: .75rem .35rem;
    font-size: 1.5rem;
    color: #000;
    border-bottom: 1px solid #cacaca;
    width: 100%;
    display: flex;
  }
`
