import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { SliderItem } from './data';
import BannerItem from './Component/BannerSlider';
const Home = props => {
    const [state, setState] = useState({
        slider: [],
    })
    console.log('slider', state.slider);
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem 
        })
    },[]);
    
    console.log('slider', state.slider);
    return(
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index}/>
                        })}
                    </Slider>
                </BannerWrapper>
            </div>

        </HomeWrapper>
    );
}
export default Home;
