import styled from "styled-components";
export const ProductCartItems = styled.div`
  display: flex;
  padding: 1rem;
  color: #000;
  border-bottom: 1px solid #cacaca;
  & > img {
    width: 15%;
  }
  .info-right {
    display: flex;
    height: max-content;
    .info {
      padding: 0 1rem;
      max-width: 60%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      & > .name-product {
        font-size: ;
      }
      & > .relate-info {
        color: #757575;
        line-height: 1;
        font-size: 12px;
      }
    }
    & > .quantity {
      display: flex;
      height: max-content;
      padding: 0 1rem;
      .quantity-text {
        padding: 0.25rem 0.55rem;
      }
    }
    & > .price {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      align-items: flex-start;
    }
    & > .btn-delete{
      display: flex;
      align-items: flex-start;
      color: #ee4d2d;
      font-size: 1.3rem;
      & > svg {
        margin-right: .35rem;

      }
    }
  }
`;
