import styled from "styled-components";
export const InnerItems = styled.div`
    transition: .3s;
    padding: 0.75rem 1rem;
    position: relative;
    &::after,&:before {
        position: absolute;
        content: '';
        border: 0.1px solid #ddd;
    } 
    &::after {
        top: 0;
        left: 0;
        width: 0;
        height: 0;
        transition: .3s;
    }
    &::before {
        bottom: 0;
        left: 0;
        width: 0;
        height: 0;
        transition: .4s;
    }
    .name-inner {
        text-align: center;
        margin: 1rem 0 2rem;
        font-weight: 700;
        text-transform: uppercase;
    }
    .images  {
        width: 100%;
        flex-grow: 1;
        overflow: hidden;
        display: inline-block;
        &:hover > img {
            transform: scale(1.1);
         }
       & > img {
        width: 100%;
        max-height: 165px;
        object-fit: cover;
        display: flex;
        flex-direction: column;
        transition: .3s;
        cursor: pointer;
        transform: scale(0.9);
        }
    }
`
