import React, { useState, useEffect } from 'react';
import ReactPaginate from 'react-paginate';
import { PaginationPageWrapper } from './style';
import ProductItem from '../../container/Home/Component/ProductItem';
import _slice from 'lodash/slice';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faAngleLeft,faAngleRight} from '@fortawesome/free-solid-svg-icons';
const PaginationPage = props => {
    const { product } = props;
    const page = _slice(product, 0,);
    const [pageNumber, setPageNumber] = useState(0);
    const productsPerPage = 9;
    const pagesVisited = pageNumber * productsPerPage;
    const pageCount = Math.ceil(page.length / productsPerPage);
    const changePage = (data) => {
        const { selected } = data
        setPageNumber(selected);

    }
    const [isLoading, setIsLoading] = useState(true);
    useEffect(() => {
        setIsLoading(true);
        setTimeout(() => {setIsLoading(false)}, 3000);

    },[])
    if(isLoading) return  <div className="loading" style={{
        flexGrow: 1,
        height: "100vh",
        overflow: 'hidden'
      }}>
        <img
          style={{
            margin: '5rem auto',
            display: 'block',
            width: '20%'
          }}
          src="/assets/images/logo/load.gif"
          alt="loading" />
      </div>
    return (
        <PaginationPageWrapper className="paginaion container-fluid">
            <div className="row">
                {page.slice(pagesVisited, pagesVisited + productsPerPage).map((item, index) => {
                    return item.name !== '' ?
                        <ProductItem nameButton="Xem thông tin sản phẩm" icon={item.discountContent} item={item} key={index} className="col-lg-4 col-md-6 product-item" /> :
                        <div className="empty">
                            <img src="/Assets/Images/product/empty_product.svg" alt="empty" />
                            <p className="empty-text">
                                Không tìm thấy sản phẩm
                            </p>
                        </div>
                })}


            </div>
            {page.slice(pagesVisited, pagesVisited + productsPerPage).length ?  <ReactPaginate
                    previousLabel={<FontAwesomeIcon icon={faAngleLeft}/>}
                    nextLabel={<FontAwesomeIcon icon={faAngleRight}/>}
                    pageCount={pageCount}
                    breakLabel={'...'}
                    onPageChange={changePage}
                    containerClassName={"paginationBttns"}
                    previousLinkClassName={"previousBttn"}
                    nextLinkClassName={"nextBttn"}
                    disabledClassName={"paginationDisabled"}
                    activeClassName={"paginationActive"}
                ></ReactPaginate> : ''}
        </PaginationPageWrapper>
    );
}
export default PaginationPage;
