import styled from "styled-components";
export const HeaderTopItems = styled.li`
  position: relative;
  &:hover:after {
    color: #fff;
  }
  &:hover > a {
    color: #ee4d2d;
  }
  & > a {
    color: #fff;
    padding: 0.35rem 1rem;
    display: block;
    text-transform: uppercase;
    transition: all 0.3s linear;
    position: relative;
    &:hover > .quantity-cart {
      color: #fff;
    }
    & > .quantity-cart {
      position: absolute;
      top: 40%;
      left: 55%;
      transform: translate(-50%, -50%);
      color: #000;
      transform: .3s;
    }
    & > svg {
      font-size: 1.7rem;
      z-index: 3;
    }
    &.active-menu {
      color: #ee4d2d;
      font-size: 1.2rem;
    }
    &:before {
      content: "${(props) => props.name === "đăng nhập"? "\f007": props.name === "đăng ký"? "\f234": ""}";
      margin-right: ${(props) => (props.type === "account" ? ".55rem" : "0")};
      font-family: Fontawesome;
    }
  }
`;
