import styled from "styled-components";
export const HeaderTopItems = styled.li`
  & > a {
    color: #fff;
    padding: ${props => props.type === "cart" ? '.45rem' : ".35rem 1rem"};
    display: block;
    text-transform: uppercase;
    transition: all .3s linear;
    border: ${props => props.type === "cart" ? '1.5px solid #ee4d2d' : 'none'};
    &.active-menu {
      color: #ee4d2d;
      font-size: 1.2rem;
    }
    &:hover {
      color: #ee4d2d;
    }
    &:before {
      content: "${props => props.name === 'đăng nhập' ? '\f007' : (props.name==="đăng ký" ? '\f234' : '')}";
      margin-right: ${props => props.type === 'account' ?  '.55rem' : '0'};
      font-family: Fontawesome;
    }
  }
`
