import React, { useEffect } from "react";
import { CartWrapper } from "./style";
import {
  getListProductCartRequest
} from "./actions";
import { connect } from "react-redux";
import {
  selectCart,
  selectIsLoading
} from "./selector";
import Loading from "../Components/Loading";
import Empty from "../Components/Empty";

const Cart = props => {
  const {
    handleGetListProductCart,
    productsCart,
    isLoading,
    history
  } = props;
  useEffect(() => {
    handleGetListProductCart();
  }, [])
  if(isLoading) return <Loading />
  console.log('product', productsCart);
  const {
    color,
    name,
    price,
    quantity,
    size
  } = productsCart;
  return(
    <CartWrapper className="cart">
      {productsCart.length ? <div>ABC</div> : <Empty history={history}/>}

    </CartWrapper>
  );
}

const mapStateToProps = state => {
  return {
    productsCart: selectCart(state),
    isLoading: selectIsLoading(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductCart: () => dispatch(getListProductCartRequest())
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (Cart);
