import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import { MenuItems, SubMenuItems } from "./style";
export const SubMenuItem = ({ subMenuItem }) => {
  const { name, path } = subMenuItem;
  return (
    <SubMenuItems className="sub-menu-item">
      <NavLink
        className="sub-menu-link"
        activeClassName="active"
        to={path}
        exact>
        {name}
      </NavLink>
    </SubMenuItems>
  );
}
const MenuItem = ({ menuItem, type }) => {
  const { name, path, icon, subMenu } = menuItem;
  const [test, setTest] = useState(true);
  const abc = document.querySelector('.sub-menu');
  const handleToggleClass = () => {
    if(subMenu.length) {
      setTest(false)
      console.log('test', test);
    }
    else {
      setTest(true);
      console.log('test', test);
    }
  }
  return (
    <MenuItems
      className="menu-item"
      type={type}
      subMenu={subMenu}>
      <NavLink
        to={path}
        exact
        className="menu-link"
        activeClassName="active"
        onClick={handleToggleClass}
      >
        {icon}
        {name}
      </NavLink>
      {subMenu && <ul className="sub-menu">
        {subMenu.map(item => {
          return <SubMenuItem subMenuItem={item} key={item.id} />
        })}
      </ul>}
    </MenuItems>
  );
}
export default MenuItem;
