import styled from "styled-components";
export const HeaderTopItems = styled.li`
  & > a {
    color: #fff;
    padding: .35rem 1rem;
    display: block;
    text-transform: uppercase;
    transition: all .3s linear;
    position: relative;
    &:after {
        content: '${props => props.type === 'cart' && '0'}';
        color: red;
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 0;
    }
    & > svg {
      font-size: 1.7rem;
    }
    &.active-menu {
      color: #ee4d2d;
      font-size: 1.2rem;
    }
    &:hover {
      color: #ee4d2d;
    }
    &:before {
      content: "${props => props.name === 'đăng nhập' ? '\f007' : (props.name==="đăng ký" ? '\f234' : '')}";
      margin-right: ${props => props.type === 'account' ?  '.55rem' : '0'};
      font-family: Fontawesome;
    }
  }
`
