import styled from "styled-components";
export const SearchProductItems = styled.div`
    margin: 0.75rem 0;
    display: flex;
    align-items: center;
    justify-content: space-between;

    &> img {
        width: 30%;
    }
    .info {
        width: 60%;
        .name-product {
            font-size: 1.2rem;
        }
        .price {
            color: red;
            position: relative;
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
        }
        .discount {
            text-decoration:line-through;
            color: #434E5E,
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
        }
    }
`
