import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './Component/Header';
import Login from './Component/login';
import Register from './Component/register';
import Home from './container/Home';
import FooterWare from './container/Product/Component/FootWare';
import Footer from './Component/Footer';
import Introduce from './Component/Introduce';
import Clothes from './container/Product/Component/Clothes';
import Accessory from './container/Product/Component/Accessory';
import { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faAngleUp, faCog} from '@fortawesome/free-solid-svg-icons'
import { ChangeColor } from './style';
const App = props => {
  const [scrollToTop, setScrollToTop] = useState(false);
  const [currentColor, setCurrentColor] = useState(0);
  useEffect(() => {
    const hideScollToTop = () => {
      setScrollToTop(window.scrollY > 100);
    }
    window.addEventListener("scroll", hideScollToTop);
    return () => {
      window.removeEventListener("scroll", hideScollToTop);
    }
    // clean component
    // mount and unmount
  }, []);
  const scrollTop = () => window.scrollTo({
    top: 0,
    behavior: 'smooth'
  })
    useEffect(() => {
      let body = document.querySelector("body");
      const iconSetting = document.querySelector(".icon-setting");
      iconSetting.addEventListener("click", () => {
        this.classList.add("rotate-setting");
      })
    body.addEventListener("click", () => {
      this.classList.add("rotate-setting");
    })
    },[])
  return(
    <div className="app">
      <Router>
        <Header/>
        {scrollToTop && <span style={{
          position: 'fixed',
          bottom: 0,
          right: '50%',
          cursor:  'pointer',
          width: '30px',
          height: '30px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: '1.3rem',
          transition: '.3s',
          zIndex: '99999'
        }} onClick={scrollTop} className="scroll-to-top">
          <FontAwesomeIcon icon={faAngleUp}/>
          </span>}
        <ChangeColor>
          <span className="icon-setting">
            <FontAwesomeIcon icon={faCog}/>
          </span>
        </ChangeColor>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/login" component={Login}></Route>
          <Route exact path="/register" component={Register}></Route>
          <Route exact path="/gioi-thieu" component={Introduce}></Route>
          <Route exact path="/giay-dep" component={FooterWare}></Route>
          <Route exact path="/quan-ao" component={Clothes}></Route>
          <Route exact path="/phu-kien-khac" component={Accessory}></Route>
        </Switch>
        <Footer />

      </Router>
    </div>
  );
}

export default App;
