import styled from "styled-components";
export const MenuItems = styled.li`

  & > .menu-link {
    display: ${props => props.type ? 'flex' : 'block'};
    flex-direction: column;
    align-items: center;
    padding: .75rem 1rem;
    color: #000;
    text-transform: uppercase;
    font-weight: 550;
    position: relative;
    &:after {
      content: '${props => props.subMenu ? '\f107' : ''}';
      font-family: Fontawesome;
      margin-left: .35rem;
    }
    & > svg {
      font-size: 1.2rem;
      margin: .35rem 0;
    }
    &.active {
      color: #ee4d2d;
    }
  }

`
export const SubMenuItems = styled.li`
  .sub-menu-link {
    color: #000;
    font-weight: 550;
    text-transform: uppercase;
    font-size: 1rem;
    padding: .75rem 1rem;
    &.active {
      color: #ee4d2d;
      font-size: 1.1rem;
    }
}
`
