import styled from "styled-components";
export const SearchProductItems = styled.div`
    margin: 0.75rem 0;
    display: flex;
    align-items: center;
    justify-content: space-between;

    &> img {
        width: 30%;
    }
    .info {
        width: 60%;
        .name-product {
            font-size: 1.2rem;
        }
        .price {
            color: red;
            position: relative;
            font-size: 1.3rem;
            margin-left: 0.25rem;
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
        }
        .discount {
            text-decoration:line-through;
            color: #434E5E;
            display: ${props => props.discount === '' ? 'none' : 'inline-block'};
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
            
        }
    }
`
