import { HANDLE_INIT_PRODUCT_TABS, HANDLE_INIT_PRODUCT_INNER } from "./constants";
const initialState = {
    productsTabs: [],
    listInnerProduct: []
}
const productReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'HANDLE_INIT_PRODUCT_INNER': {
            console.log('abc', action.payload);

            return {
                ...state,
                listInnerProduct: action.payload
            }
        }
        case 'HANDLE_INIT_PRODUCT_TABS': {
            return {
                ...state,
                productsTabs: action.payload
            }
        }
        default: 
        return state;
    }
}
export default productReducer;
