import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper, ListProductWrapper, TabItems, CarouselProductWrapper, BannerDiscountWrapper, BodyLeftWrapper, BodyRightWrapper, ShoppingUnitItems, SellingProduct, HotlineWrapper, ProductTabWrapper } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct, ProductTabs, ShippingUnits } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem';
import { NavLink } from 'react-router-dom';
import ProductItem from './Component/ProductItem';
import _, { random } from 'lodash';
import _uniq from 'lodash/uniq';
import _filter from 'lodash/filter';
import _intersection from 'lodash/intersection'
import './style.css';
import { connect } from 'react-redux';
import { handleInitProductTabs, handleInitProductInner } from './actions';
import Title from './Component/Title';
import Buttons from '../../Component/Button';
import TitlePage from '../../Component/Title';
import BannerImageItem from './Component/BannerImageItem';
import SellingItem from './Component/SellingItem';
import Button from './../../Component/Button'
const TabItem = props => {
    const { ind, item, onClick, classActive, status } = props;

    return (
        <TabItems className={`tab-item ${classActive}`} status={status} onClick={onClick} >
            {item}
        </TabItems>
    );


}
export const ShoppingUnitItem = ({ shoppingUnitItem: { name } }) => {
    return (
        <ShoppingUnitItems className="shopping-unit-item">
            <img src={name} alt="shopping unit" />
        </ShoppingUnitItems>
    );
}
const Home = props => {
    const { handleInitListProductInner, productReducer, handleInitListProductTabs, history } = props
    // lấy data trong productReducer (trong store)
    const { productTabs, listInnerProduct } = productReducer;
    const [state, setState] = useState({
        slider: [],
        shippingUnits: [],
        status: true
    })
    // nút chọn các mặt hàng ở phần tabs
    const [filterTabs, setFilterTabs] = useState([]);

    // xử lý loading
    const [isLoading, setIsLoading] = useState(true);
    // get data
    useEffect(() => {
        setIsLoading(true);
        setTimeout(() => { setIsLoading(false) }, 3000)
        setState({
            ...state,
            slider: SliderItem,
            shippingUnits: ShippingUnits
        })
        handleInitListProductInner(ListInnerProduct);
        handleInitListProductTabs(ProductTabs)
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
          })
    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    const listTabMenu = ['sản phẩm mới', 'sản phẩm bán chạy', 'sản phẩm phổ biến']
    // xử lý tabs carousel
    const Tabs = {
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 1000,
        autoplaySpeed: 2000,
        cssEase: "linear",
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 575,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            },
        ]
    }

    const productTab = {
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 1,
