import React from "react";
import { MessageCartWrapper } from "./style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons"
const MessageCart = ({productCart}) => {
  const {
    name,
    avatar,
    size,
    quantity,
    color
  } = productCart;
  return (
    <MessageCartWrapper className="message-cart">
      <div className="message-content">
        <span className="close-message">
          <FontAwesomeIcon icon={faTimes} />
        </span>
        <div
          className="content">
          <div className="product-to-cart">
            <p className="message-text">
              1 sản phẩm mới được thêm vào giỏ hàng của bạn
            </p>
            <div className="product-cart">
              <img src="avatar" alt=""/>
              <div className="info">
                <h3 className="name-product">
                  {name}
                </h3>
                <p className="relate-info">
                  
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </MessageCartWrapper>
  );
}
export default MessageCart;
