import styled from "styled-components";
export const ErrorWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1.5rem 0;
  width: 100%;
  height: 100vh;
  .error-text {
    font-size: 1.75rem;
  }
`
